  ButtonGroup
} from '@mui/material';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';

// Dictionary for mapping numeric codes to human-readable values
const valueMappings = {
  Gender: {
    0: 'Male',
    1: 'Female'
  },
  Ethnicity: {
    0: 'Caucasian',
    1: 'African American',
    2: 'Asian',
    3: 'Other'
