  Confusion: {
    0: 'No',
    1: 'Yes'
  },
  Disorientation: {
    0: 'No',
    1: 'Yes'
  },
  PersonalityChanges: {
    0: 'No',
    1: 'Yes'
  },
  DifficultyCompletingTasks: {
    0: 'No',
    1: 'Yes'
  },
  Forgetfulness: {
    0: 'No',
    1: 'Yes'
  }
};

// Initial values for healthy patient
const healthyPatientProfile = {
  Age: 55.0,
  Gender: 1.0,
  Ethnicity: 0.0,
  EducationLevel: 3.0,
  BMI: 22.0,
  Smoking: 0.0,
  AlcoholConsumption: 0.0,
  PhysicalActivity: 1.0,
  DietQuality: 5.0,
  SleepQuality: 5.0,
  FamilyHistoryAlzheimers: 0.0,
  CardiovascularDisease: 0.0,
  Diabetes: 0.0,
  Depression: 0.0,
  HeadInjury: 0.0,
  Hypertension: 0.0,
  SystolicBP: 118.0,
  DiastolicBP: 75.0,
  CholesterolTotal: 180.0,
  CholesterolLDL: 95.0,
  CholesterolHDL: 60.0,
  CholesterolTriglycerides: 120.0,
  MMSE: 30.0,
  FunctionalAssessment: 10.0,
